import React, { useState, useEffect } from 'react';
import { useAuth } from '../Auth/Store_Token/Utils_Token';

const AdminDashboard = () => {
    const [contacts, setContacts] = useState([]);
    const { Authorization } = useAuth(); 

    useEffect(() => {
        const fetchContacts = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/form/getall', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': Authorization
                    }
                });
                const data = await response.json();
                setContacts(data.alluser);
            } catch (error) {
                console.error(error);
            }
        };
        fetchContacts();
    }, [Authorization]);

    const handleDelete = async (id) => {
        try {
            await fetch(`http://localhost:5000/api/form/contact/delet/${id}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': Authorization
                }
            });
            setContacts(contacts.filter(contact => contact._id !== id));
        } catch (error) {
            console.error(error);
        }
    };

    const handleUpdate = async (id, newData) => {
        try {
            await fetch(`http://localhost:5000/api/form/users/update/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': Authorization
                },
                body: JSON.stringify(newData)
            });
            setContacts(contacts.map(contact => (contact._id === id ? { ...contact, ...newData } : contact)));
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <h2>Contact List</h2>
            <ul>
                {contacts.map((contact) => (
                    <li key={contact._id}>
                        <strong>{contact.username}</strong> - {contact.email} - {contact.phone} - {contact.msg}
                        <button onClick={() => handleDelete(contact._id)}>Delete</button>
                        <button onClick={() => handleUpdate(contact._id, { username: 'New Name' })}>Update</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDashboard;
