import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Register.css';

export default function Registor() {
  const navigate = useNavigate();

  const [user, setUser] = useState({
    username: "",
    email: "",
    password: ""
  });

  const Changeelemet = (e) => {
    let { name, value } = e.target;

    setUser({
      ...user,
      [name]: value
    });
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`http://localhost:5000/api/auth/register`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(user),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText);
      }

      const data = await response.json();
      alert("Registration successful");
      setUser({ username: "", email: "", password: "" });
      navigate("/login");
      console.log(data);
    } catch (error) {
      console.error("Error", error);
      alert(`Registration failed: ${error.message}`);
    }
  }

  return (
    <div className='register-container'>
      <div className='register-header'>
        <h1>Register Form</h1>
      </div>

      <form onSubmit={handleSubmit}>
        <div className='register-form'>
          <label htmlFor='username'>Username</label>
          <input type='text' name='username' placeholder='Enter your username' required value={user.username} onChange={Changeelemet} />
        </div>

        <div className='register-form'>
          <label htmlFor='email'>Email</label>
          <input type='email' name='email' placeholder='Enter your email' required value={user.email} onChange={Changeelemet} />
        </div>

        <div className='register-form'>
          <label htmlFor='password'>Password</label>
          <input type='password' name='password' placeholder='Enter your password' required value={user.password} onChange={Changeelemet} />
        </div>

        <br />

        <button type="submit" className="btn">Register Here</button>
      </form>
    </div>
  );
}
