import React, { useState } from "react";
import axios from "axios";
import Talk from "./Talk";
import { useAuth } from "./Store_Token/Utils_Token";

const Contact = () => {
  const { Authorization } = useAuth();
  const [data, setData] = useState({
    fullname: '',
    email: '',
    phone: '',
    msg: '',
  });

  const InputEvent = (event) => {
    const { name, value } = event.target;
    setData((preVal) => {
      return {
        ...preVal,
        [name]: value,
      };
    });
  };

  const formSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/form/contact', {
        fullName: data.fullname,
        email: data.email,
        phone: data.phone,
        msg: data.msg
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': Authorization  // Use the Authorization token from context
        }
      });
      alert('Message sent successfully');
    } catch (error) {
      console.error('There was an error sending the message!', error);
    }
  };

  return (
    <>
      <div className="my-5">
        <h1 className="text-center">Contact IN</h1>
      </div>
      <div className="contact_div">
        <div className="row">
          <div className="col-md-6 col-10 mx-auto">
            <form onSubmit={formSubmit}>
              <div className="mb-3">
                <label htmlFor="exampleFormControlInput1" className="form-label">
                  Full Name:
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="exampleFormControlInput1"
                  name="fullname"
                  value={data.fullname}
                  onChange={InputEvent}
                  placeholder="Enter your name"
                />
              </div>
              <div className="mb-3">
                <label htmlFor="exampleFormControlInput1" className="form-label">
                  Email address
                </label>
                <input
                  type="email"
                  className="form-control"
                  id="exampleFormControlInput1"
                  name="email"
                  value={data.email}
                  onChange={InputEvent}
                  placeholder="name@example.com"
                  required
                />
              </div>
              <div className="mb-3">
                <label htmlFor="exampleFormControlInput1" className="form-label">
                  Mobile No.:
                </label>
                <input
                  type="tel"  // Changed to "tel" for mobile numbers
                  className="form-control"
                  id="exampleFormControlInput1"
                  name="phone"
                  value={data.phone}
                  onChange={InputEvent}
                  placeholder="+91"
                />
              </div>
              <div className="mb-3">
                <label htmlFor="exampleFormControlTextarea1" className="form-label">
                  Message
                </label>
                <textarea
                  className="form-control"
                  id="exampleFormControlTextarea1"
                  rows="3"
                  name="msg"
                  value={data.msg}
                  onChange={InputEvent}
                ></textarea>
              </div>
              <div className="col-12">
                <button className="btn btn-outline-primary" type="submit">Submit form</button>
              </div>
            </form>
          </div>
        </div>
      </div>
      <Talk />
    </>
  );
};

export default Contact;
